class Solution:
    def lastSubstring(self, s: str) -> str:
        N = len(s)
        ans = N - 1
        maxc = s[ans]
        maxn = 1
        curn = 1
        for i in range(N - 2, -1, -1):
            if s[i] < maxc:
                curn = 0
                continue
            if s[i] > maxc:
                maxc = s[i]
                ans = i
                maxn = 1
                curn = 1
                continue
            curn += 1
            if curn < maxn:
                continue
            if curn > maxn:
                ans = i
                maxn = curn
                continue

            if s[i:i + N - ans] >= s[ans:]:
                ans = i

        return s[ans:]

s = Solution()
